import React from "react";
import recipes from "../menuDescriptions";
import Swal from "sweetalert2";

const Menu = () => {
//   console.log(recipes[0]);
const handleOrder = (id) => {
    console.log(id);

    const swalWithBootstrapButtons = Swal.mixin({
        customClass: {
          confirmButton: 'btn btn-success',
          cancelButton: 'btn btn-danger'
        },
        buttonsStyling: false
      })

      swalWithBootstrapButtons.fire({
        title: 'Do you want to confirm your order?',
        text: "You won't be able to change the order once submitted.",
        icon: 'warning',
        showCancelButton: false,
        confirmButtonText: 'Yes, please confirm order.',
        reverseButtons: true
      }).then((result) => {
        if (result.isConfirmed) {
          swalWithBootstrapButtons.fire(
            'Ordered!',
            'Your order has been confirmed.',
            'success'
          )
        }
      })

}
  return (
    <div className="menu-container">
      <div className="menu-header">
        <h2>This weeks specials!</h2>
        <button>Online Menu</button>
      </div>
      <div className="cards">
        {recipes.map((recipe) => (
          <div key={recipe.id} className="menu-items">
            <img src={recipe.image} alt="" />
            <div className="menu-content">
              <div className="heading">
                <h5>{recipe.title}</h5>
                <div className = "price"><p>${recipe.price}</p></div>
              </div>
              <p>{recipe.description}</p>
              <button className="orderbtn" onClick={() => handleOrder(recipe.id)}>Order a delivery</button>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
};

export default Menu;
